@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  --color-tint: #f8fbff;

  --font-heading: "Space Grotesk", sans-serif;
  --font-body: "Open Sans", sans-serif;

  /* Colors */
  --color-base: #183b56;
  --color-brand: #1565d8;
  --color-accent: #36b37e;
  --color-accent-2: #0d2436;
  --color-light: #fcfcfd;
  --color-shade: #cbcace;
  --color-inverted: white;
  --color-tint: #e5eaf4;

  /* Base values */
  --color: var(--color-base);
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #eee;
  --background: white;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--font-body);
  color: var(--color-base);
  line-height: 1.2;
  font-size: 1.125rem;
  background: var(--background);
}

.section.has-content {
  display: flex;
  justify-content: center;
  padding: 5rem 2rem;

  .content {
    max-width: 800px;
    width: 100%;
  }
}

.section-container {
  max-width: 1250px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

.heading-group {
  display: grid;
  gap: 1rem;
  place-content: center;
  text-align: center;

  .superhead {
    font-family: var(--font-body);
    color: var(--color-accent);
    font-size: 0.875rem;
    font-weight: 500;
    letter-spacing: 1.5px;
    text-transform: uppercase;
  }

  .subheading {
    color: #4f6373;
    line-height: 1.4;
    max-width: 600px;
    font-weight: 400;
    max-width: 600px;
    margin: 0 auto;
  }
}

.heading {
  font-family: var(--font-heading);
  font-size: 2rem;
  line-height: 1.1;
  font-weight: 500;
  max-width: 600px;
}

.button {
  color: var(--color-brand, white);
  background: var(--color-inverted);
  border: 2px solid var(--color-brand);
  border-radius: 6px;
  padding: 8px 20px;
  transition: 0.1s background, 0.1s color;

  &:hover {
    color: var(--color-inverted);
    background: var(--color-brand);
    border-color: var(--color-inverted);
  }

  &.inverted {
    background: var(--color-white);
    color: var(--color-brand);
    border-color: #0d2436;
  }
}

.link {
  font-size: 1.125rem;
  font-weight: 400;
  color: var(--color-brand);

  .arrow {
    transition: transform 0.1s;
  }

  &:hover .arrow {
    transform: translateX(4px);
  }
}

