code.css:
section {
  font-family: 'Open Sans';
}
.section-container{
  display:flex;
  gap: 40px;
  margin-top:44px;
  line-height: 30px;

}
.info{
  display: flex;
  width: 100%;
  padding: 16px;
  flex-direction: column;
  align-items: flex-start;
  gap: 6px;
  border-radius: 6px;
  background: var(--Grey-2, #EEF2FF);
}

.infoText{
  color:  #27283B;
font-size: 15px;
font-style: normal;
line-height: 26px; 
font-weight:300;

}
.box1{
  border-right: 1px solid #E2E8F0;
  width:300px;
  height: 650px;
}
.item-icon{
  display: flex;
  gap: 10px;
}
.accordion {
  width: 100%;
  max-width: 290px;

  .item {
    padding: 1rem 0;

    &.active button .icon {
      transform: scaleY(-1);
    }

    button {
      display: flex;
      justify-content: space-between;
      align-items: center;
      width: 100%;

      span {
        font-size: 1.25rem;
        font-weight: 600;
        text-align: left;
      }

    .icone {
        font-size: 1rem;
        color: #1E293B;
        transition: transform 0.2s;
      }

      & + .description {
        padding-top: 0.5rem;
      }
    }

    .description {
      max-width: 800px;
      font-size: 1.125rem;
      padding-right: 2rem;
      padding-left: 2rem;
      color: var(--Primary-2, #7B5CF5);

    }
  }
}
.menu{
   display: flex;
   font-size: 1.25rem;
   font-weight: 600;
   text-align: left;
  justify-content: start;
}
.box2{
  display: flex;
  width:780px;
  flex-direction: column;

}

.menu-icon{

  border-radius: 4px;
  border: 0.5px solid #E2E8F0;
  display: flex;
  width: 24px;
  height: 24px;
  padding: var(--Number, 2px);
  justify-content: center;
  align-items: center;
  flex-shrink: 0;

}

.heading-group {
  display: grid;
  text-align: start;
  justify-content: start;
  border-bottom: 1px solid  #E2E8F0;
  padding-bottom:20px;

  .heading{
    font-family: 'Lato', sans-serif;
    font-size: 32px;
    font-style: normal;
    line-height: 46px; /* 192.188% */;
    font-weight:700;
  }
}
.content{
  display:flex;
  flex-direction: column;
  margin-top:24px;
  gap:40px
}
.subtitle{
  color: var(--Contrast-1, #27283B);
  font-size: 16px;
font-style: normal;
font-weight: 600;
  margin-top:20px;
line-height: 24px; /* 109.091% */
}

.paso1{
  display:flex;
  flex-direction: column;
  gap: 5px;
  font-family: 'Lato', sans-serif;

  p{
    line-height: 30px;
    font-size: 16px;
    font-family:'Open Sans';
  }

}
.link{
  color: #7B5CF5;
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: 30px;
  text-decoration-line: underline;
}
.tutoriales {
   font-size: 1.25rem;
    font-weight: 600;
    text-align: left;
  margin-bottom:12px

}

.letter{

  font-size:16px;
  margin-left:25px;
  list-style-type: lower-alpha;
   padding-left: 0; 
  font-family:'Open Sans';
}

@media (max-width: 600px){
  .box1{
    display:none;
  }
  .box2{
    width:100%;
    display:flex;
    justify-content:center;

  }
  .steps{
    width:100%;
    font-size:14px;
    justify-content:center;
  }
}

