import Icon from '@iconify/svelte';

let images = [
    'https://bvyolarusyudhhaxhyjk.supabase.co/storage/v1/object/public/images/8762b14d-dc88-46a2-89e9-945b4c930503/1713908336132feature_1-2.webp',
    'https://bvyolarusyudhhaxhyjk.supabase.co/storage/v1/object/public/images/8762b14d-dc88-46a2-89e9-945b4c930503/1713891294196bot1-_4_.webp',
    'https://bvyolarusyudhhaxhyjk.supabase.co/storage/v1/object/public/images/8762b14d-dc88-46a2-89e9-945b4c930503/1713891540733bot1%20(3).webp',
    'https://bvyolarusyudhhaxhyjk.supabase.co/storage/v1/object/public/images/8762b14d-dc88-46a2-89e9-945b4c930503/1713905403706web%20(1)-2.webp',
    'https://bvyolarusyudhhaxhyjk.supabase.co/storage/v1/object/public/images/8762b14d-dc88-46a2-89e9-945b4c930503/1713890782473wp-_1_.webp',
    'https://bvyolarusyudhhaxhyjk.supabase.co/storage/v1/object/public/images/8762b14d-dc88-46a2-89e9-945b4c930503/1713919696000historial%20(1)-2.webp'
  ];
  let imageObjects = [];
  let currentImage = images[0];
  let activeCard = null;

  function preloadImages() {
    for (let i = 0; i < images.length; i++) {
        imageObjects[i] = new Image();
        imageObjects[i].src = images[i];
    }
}

  function changeImage(event, index) {
    currentImage = images[index];
    if (activeCard) {
      activeCard.style.background = "";
      activeCard.querySelector('.title').style.color = "";
      activeCard.querySelector('.icon').style.color = "";
    }
    activeCard = event.currentTarget.parentNode;
    activeCard.style.background = "var(--Grey-1, #E2E8F0)";
    activeCard.querySelector('.title').style.color = "#603FDF";
    activeCard.querySelector('.icon').style.color = "#603FDF";
  }

  window.onload = function () {
    preloadImages();
    setTimeout(() => {
    let firstCard = document.querySelector('.cards li');
    if (firstCard) {
      activeCard = firstCard;
      activeCard.style.background = "var(--Grey-1, #E2E8F0)";
    activeCard.querySelector('.title').style.color = "#603FDF";
    activeCard.querySelector('.icon').style.color = "#603FDF";
    }
  }, 0);

  };

